import {createSlice} from '@reduxjs/toolkit'

export const darkModeSlice = createSlice({
    name: 'darkMode',
    initialState : {value : {
        isDarkMode : false
    }},
    reducers : {
        change : (state,action) => {
            state.value = action.payload
        }
    }
})

export const {change} = darkModeSlice.actions;
export default darkModeSlice.reducer;
