import React, { useState } from 'react'
import SearchCustomer from './SearchCustomer'
import { useDispatch } from 'react-redux'
import { change } from '../features/darkMode'
import Navbar from './Navbar'
import Catergory from './Catergory'
import BreadCrumb from './BreadCrumb'
import { collection, addDoc,query} from "firebase/firestore"; 
import swal from 'sweetalert';
import { db } from '../Firebase'
import SearchArea from './SearchArea'

const Settings = () => {
  const dispatch = useDispatch()
  const [isAreaClicked, setIsAreaClicked] = useState(true)
  const [isDarkBtnClick, setIsDarkBtnClick] = useState(false)
  const [areaName, setAreaName] = useState('')
  const [district,setDistrict ] = useState('')
  const [city,setCity] = useState('')
  const q=query(collection(db,'Area'));


  const handleAreaName = (e) => {
    setAreaName(e.target.value)
}
const handleDistrict = (e) => {
    setDistrict(e.target.value)
}
const handleCity = (e) => {
    setCity(e.target.value)
}

const handleSave =async(e) => {
  swal({
    title: "Success!",
    text: "Your Note has been saved successfully!",
    icon: "success",
    button: "ok!",
  });
try {
    const docRef = await addDoc(collection(db, "Area"), {
     areaName : areaName,
      district: district,
      city:city
    });
    // console.log("Document written with ID: ", docRef.id);
    // console.log("success");
  } catch (e) {
    console.error("Error adding document: ", e);
  }
}

  const manageArea = () => {
    setIsAreaClicked(current => !current)

  }

  const toggleDarkMode = (e) => {
    e.preventDefault();
    setIsDarkBtnClick(current => !current)
    dispatch(change({
      isDarkMode: isDarkBtnClick
    }))
  }

  return (
    <>
      <Navbar />
      <Catergory />
      <BreadCrumb />

      <div className="container-fluid" style={{ height: "100vh" }}>

        <div className='d-grid gap-2 d-flex justify-content-start align-items-center m-3 '>

          <button className='btn btn-outline-info' onClick={manageArea}>Manage Area</button>
          <button className='btn btn-outline-info me-0' onClick={toggleDarkMode}>Dark Mode</button>
        </div>
        {isAreaClicked && (
          <div>
            <div className='d-flex justify-content align-items-center' id='area-search' >
              <div className='d-flex align-items-center' style={{ marginLeft: "px" }}>
                <label htmlFor="areaName" className='me-3 label'>Area Name : </label>
                {/* <SearchCustomer /> */}
                <SearchArea />

              </div>
              <div className='btn-block d-flex  grpbtn'>
                <button className='btn btn-outline-primary m-2' data-bs-toggle="modal" data-bs-target="#addAreaModal" > Add Area &nbsp;
                  {/* <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-person-plus-fill" viewBox="0 0 16 16">
                <path d="M1 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H1zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                <path fillRule="evenodd" d="M13.5 5a.5.5 0 0 1 .5.5V7h1.5a.5.5 0 0 1 0 1H14v1.5a.5.5 0 0 1-1 0V8h-1.5a.5.5 0 0 1 0-1H13V5.5a.5.5 0 0 1 .5-.5z" />
              </svg> */}
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-geo-alt-fill" viewBox="0 0 16 16">
                    <path d="M8 16s6-5.686 6-10A6 6 0 0 0 2 6c0 4.314 6 10 6 10zm0-7a3 3 0 1 1 0-6 3 3 0 0 1 0 6z" />
                  </svg>
                </button>
                {/* <button className='btn btn-outline-warning m-2' data-bs-toggle="modal" data-bs-target="#editCustomerModal">
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                    <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                    <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z" />
                  </svg>
                </button> */}
                  <div className="modal fade" id="addAreaModal" tabIndex="-1" aria-labelledby="exampleModalLabel" data-backdrop="static" aria-hidden="true">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">Area Details</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div className="mb-3">
                                        <label htmlFor="recipient-name" className="col-form-label">Area Name :</label>
                                        <input type="text" className="form-control" id="recipient-name" onChange={handleAreaName} />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="message-text" className="col-form-label">District :</label>
                                        <input type="text" className="form-control" id="message-text" onChange={handleDistrict} />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="message-text" className="col-form-label">City :</label>
                                        <input className="form-control" id="message-text" onChange={handleCity} />
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary" onClick={handleSave}>Save</button>
                            </div>
                        </div>
                    </div>
                </div>
                <button className='btn btn-outline-danger m-2' data-bs-toggle="modal" data-bs-target="#deleteCustomerModal">
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                    <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  )
}

export default Settings
