import React, { useEffect, useState } from 'react'
import '../index.css'
import BreadCrumb from './BreadCrumb'
import Navbar from './Navbar'
import { collection, addDoc,getDocs,onSnapshot,query} from "firebase/firestore"; 
import {db} from '../Firebase'
import NotesList from './NotesList';
import swal from 'sweetalert';


const Notes = () => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [isClicked, setIsClicked] = useState(false)
    const date = new Date().toLocaleString() ;
    const [notesData,setNotesData] = useState([])

    const q=query(collection(db,'Notes'));


    useEffect(() => {
        onSnapshot(q,(snapshot)=>{
        setNotesData(snapshot.docs.map(doc=>({
        id: doc.id,
        item: doc.data()
        })))
        })
        },[isClicked]);
   

    const handleTitle = (e) => {
        setTitle(e.target.value)
    }
    const handleDescription = (e) => {
        setDescription(e.target.value)
    }

    const handleSave = async(e) => {
        e.preventDefault();
        setIsClicked(current => !current)
        swal({
            title: "Success!",
            text: "Your Note has been saved successfully!",
            icon: "success",
            button: "ok!",
          });
        try {
            const docRef = await addDoc(collection(db, "Notes"), {
              title: title,
              description: description,
              date: date
            });
   
          } catch (e) {
            console.error("Error adding document: ", e);
          }
    }

    return (
        <div className='' id="navbar">
            <Navbar />
            <BreadCrumb />
            <div className='d-flex flex-column'>
                <button type="button" id='addNoteBtn' className="btn btn-primary text-red  m-3" style={{}} data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-whatever="@mdo">Add Note <span className='ms-1'>
                    {/* <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                        <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                        <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z" />
                    </svg> */}
                    {/* <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-file-earmark-plus" viewBox="0 0 16 16">
                    <path d="M8 6.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V11a.5.5 0 0 1-1 0V9.5H6a.5.5 0 0 1 0-1h1.5V7a.5.5 0 0 1 .5-.5z"/>
                    <path d="M14 4.5V14a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V2a2 2 0 0 1 2-2h5.5L14 4.5zm-3 0A1.5 1.5 0 0 1 9.5 3V1H4a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h8a1 1 0 0 0 1-1V4.5h-2z"/>
                    </svg> */}
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-plus-square" viewBox="0 0 16 16">
                        <path d="M14 1a1 1 0 0 1 1 1v12a1 1 0 0 1-1 1H2a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1h12zM2 0a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z" />
                        <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z" />
                    </svg>
                </span></button>

                <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" data-backdrop="static" aria-hidden="true">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">Notes</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div className="mb-3">
                                        <label htmlFor="recipient-name" className="col-form-label">Title :</label>
                                        <input type="text" className="form-control" id="recipient-name" onChange={handleTitle} />
                                    </div>
                                    <div className="mb-3">
                                        <label htmlFor="message-text" className="col-form-label">Description :</label>
                                        <textarea className="form-control" id="message-text" onChange={handleDescription}></textarea>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary" onClick={handleSave}>Save</button>
                            </div>
                        </div>
                    </div>
                </div>
                {/* {isClicked && (
                    <div className="card text-bg-secondary mb-3 d-flex" style={{ maxWidth: "18rem" }} id="card">

                        <div className="card-body">
                            <h5 className="card-title" id='card-title'>{title}</h5>
                            <p className="card-text" id="card-description">{description}</p>
                        </div>
                        <div className="card-footer">
                            <p className='text-end'>{date}</p>
                        </div>
                    </div>
                )} */}
            </div>

            {notesData.map(item=> <NotesList key={item.id} arr={item} />)}
        </div>
    )
}

export default Notes
