import React, { useEffect, useState } from 'react'
import FilterBar from './FilterBar'
import Catergory from './Catergory'
import Navbar from './Navbar'
import BreadCrumb from './BreadCrumb'
import { collection, addDoc, getDocs, onSnapshot, query } from "firebase/firestore";
import { db } from '../Firebase'
import swal from 'sweetalert';
import {  ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import {storage} from "../Firebase"
import SearchCustomer from './SearchCustomer'
import SearchCustomerDetails from './SearchCustomerDetails'
import CustomerDetailsList from './CustomerDetailsList'


const CustomerInfo = () => {
  const [areaData, setAreaData] = useState([])
  const [area, setArea] = useState('')
  const [name, setName] = useState('')
  const [age, setAge] = useState()
  const [gender, setGender] = useState('')
  const [phoneNo, setPhoneNo] = useState('')
  const [altPhoneNo, setAltPhoneNo] = useState('')
  const [lineType, setLineType] = useState('')
  const [address, setAddress] = useState('')
  const [status, setStatus] = useState('')
  const [bussiness, setBussiness] = useState('')
  const [bussinessLocation, setBussinessLocation] = useState('')
  const [file, setFile] = useState();
  const [imgUrl, setImgUrl] = useState();

  const q = query(collection(db, 'Area'));

  useEffect(() => {
    onSnapshot(q, (snapshot) => {
      setAreaData(snapshot.docs.map(doc => ({
        id: doc.id,
        item: doc.data()
      })))
    })
  }, []);

  // console.log(areaData);


  const onImageChange = (e) => {
    // const [file] = e.target.files;
    // console.log(URL.createObjectURL(file));
    setFile(e.target.files[0]);
  };

  const handleAreaName = (e) => {
    setArea(e.target.value)
  }

  const addCustomer = async(e) => {
    e.preventDefault();
    const storageRef = ref(storage, 'Customer-Images/' + file.name);

const uploadTask = uploadBytesResumable(storageRef, file);


uploadTask.on('state_changed', 
  (snapshot) => {

    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    console.log('Upload is ' + progress + '% done');
    switch (snapshot.state) {
      case 'paused':
        console.log('Upload is paused');
        break;
      case 'running':
        console.log('Upload is running');
        break;
    }
  }, 
  (error) => {
    // Handle unsuccessful uploads
  }, 
  () => {

    getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
      console.log('File available at', downloadURL);
      setImgUrl(downloadURL)
    });
  }
);
try {
  const docRef = await addDoc(collection(db, "CustomerDetails"), {
    name : name,
    age : age,
    gender : gender,
    phoneNo : phoneNo,
    altPhoneNo : altPhoneNo,
    lineType : lineType,
    area : area,
    status : status,
    address:address,
    bussiness: bussiness,
    bussinessLocation : bussinessLocation,
    imgUrl : imgUrl
  });
  
} catch (e) {
  console.error("Error adding document: ", e);
}
swal({
    title: "Success!",
    text: "Customer Details has been saved successfully!",
    icon: "success",
    button: "ok!",
  });
}


  return (
    <>
      <Navbar />
      <Catergory />
      <BreadCrumb />

      <div className='container' style={{ height: "100vh" }}>
        <div className="row d-flex flex-row mt-2 justify-content-center align-items-center" style={{ width: "100%" }}>
   
          <div className="d-flex justify-content-center align-items-center" id="cus-info" >
            <div className='d-flex justify-content-center align-items-center'>

              <label className='text-center ms-5'>Search Customer Name : </label>
              {/* <input type="text" className="mx-5 " style={{ marginLeft: "30px" }} /> */}
              <SearchCustomerDetails />
            </div>
            <div className='btn-block d-flex  grpbtn'>
              <button className='btn btn-outline-primary m-2' data-bs-toggle="modal" data-bs-target="#addCustomerModal">Add Customer &nbsp;
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-person-plus-fill" viewBox="0 0 16 16">
                  <path d="M1 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H1zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                  <path fillRule="evenodd" d="M13.5 5a.5.5 0 0 1 .5.5V7h1.5a.5.5 0 0 1 0 1H14v1.5a.5.5 0 0 1-1 0V8h-1.5a.5.5 0 0 1 0-1H13V5.5a.5.5 0 0 1 .5-.5z" />
                </svg>
              </button>
     
              {/* <button className='btn btn-outline-danger m-2' data-bs-toggle="modal" data-bs-target="#deleteCustomerModal">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                  <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z" />
                </svg>
              </button> */}
            </div>

          </div>
          <CustomerDetailsList />
          <div className="modal fade" id="addCustomerModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog modal-dialog-scrollable">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Add New Customer</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                  <form>
                    <div className="mb-3">
                      <img src={file} alt="" className='img-fluid img-thumbnail mx-auto d-block' style={{ borderRadius: "50%", width: "100px", height: "100px" }} />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Profile Photo :</label>
                      <input type="file" className="form-control" id="recipient-name" onChange={onImageChange} />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Customer Name :</label>
                      <input type="text" className="form-control" id="recipient-name" onChange={(e)=>setName(e.target.value)}/>
                    </div>

                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Age :</label>
                      <input type="number" className="form-control" id="recipient-name" onChange={(e)=>setAge(e.target.value)}/>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Gender :</label>
                      <input type="text" className="form-control" id="recipient-name" onChange={(e)=>setGender(e.target.value)}/>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Mobile No :</label>
                      <input type="number" className="form-control" id="recipient-name" onChange={(e)=>setPhoneNo(e.target.value)}/>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Alternative Mobile No :</label>
                      <input type="number" className="form-control" id="recipient-name" onChange={(e)=>setAltPhoneNo(e.target.value)}/>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Line Type :</label>
                      <select name="line" onChange={(e) => setLineType(e.target.value)} defaultValue="daily">
                        <option value="daily">Daily</option>
                        <option value="weekly">Weekly</option>
                        <option value="monthly">Monthly</option>
                      </select>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="recipient-name" className="col-form-label">Area Name :</label> <br />
                      {/* <input type="text" className="form-control" id="recipient-name" /> */}
                      <select onChange={handleAreaName} name="fruits" id="fruit-select">
                        {areaData.map((option, index) => (
                          <option key={index} value={option.item.areaName}>
                            {option.item.areaName}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="message-text" className="col-form-label">Address :</label>
                      <textarea type="text" className="form-control" id="message-text" onChange={(e) => setAddress(e.target.value)}></textarea>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="message-text" className="col-form-label">Status :</label> <br />
                      <select name="status" onChange={(e) => setStatus(e.target.value)} defaultValue="active">
                        <option value="active" >Active</option>
                        <option value="inactive">Inactive</option>
                      </select>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="message-text" className="col-form-label">Business :</label>
                      <input className="form-control" id="message-text" onChange={(e) => setBussiness(e.target.value)}/>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="message-text" className="col-form-label">Business Location :</label>
                      <input className="form-control" id="message-text" onChange={(e) => setBussinessLocation(e.target.value)}/>
                    </div>
                  </form>

                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" className="btn btn-primary " onClick={addCustomer}>Add </button>
                </div>
              </div>
            </div>
          </div>
          <div className="modal fade" id="editCustomerModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                  ...
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" className="btn btn-primary">Save changes</button>
                </div>
              </div>
            </div>
          </div>
          <div className="modal fade" id="deleteCustomerModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                  ...
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" className="btn btn-primary">Save changes</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default CustomerInfo
