import React, { useState } from "react";
import './Login.css';
import { FaUser, FaLock, FaEnvelope } from "react-icons/fa";
import { emailValidator, passwordValidator, nameValidator } from "./regexValidator";
import { useNavigate } from "react-router-dom";

const Login = () => {


    const navigate = useNavigate();
    const [input, setInput] = React.useState({ email: '', password: '', name: '' })

    const [errorMessage, setErrorMessage] = React.useState('')
    const [successMessage, setSuccessMessage] = React.useState('')


    const [errorRegMessage, setErrorRegMessage] = React.useState('')
    const [successRegMessage, setSuccessRegMessage] = React.useState('')

    const handleChange = (e) => {
        setInput({ ...input, [e.target.name]: e.target.value })
    }

    React.useEffect(() => {
        if (localStorage.getItem('auth')) { navigate('/'); }
    }, [])

    const formLoginSubmitter = (e) => {
        e.preventDefault();
        setSuccessMessage('');
        if (!emailValidator(input.email)) {
            return setErrorMessage('Please enter valid email id.')
        }
        if (!passwordValidator(input.password)) {
            return setErrorMessage('Password should have minimum 8 character with the combination of uppercase,lowercase, numbers and specialcharacters.')
        }
        if (input.email !== "admin@a.com" || input.password !== "Password@1") {
            setErrorMessage('Invalid email or password.');
        }
        else {
            localStorage.setItem('auth', true);
            navigate('/');
        }
    }


    const formRegisterSubmitter = (e) => {
        e.preventDefault();
        setSuccessRegMessage('');

        if (!nameValidator(input.name)) {
            return setErrorRegMessage('Please enter valid name.')
        }
        if (!emailValidator(input.email)) {
            return setErrorRegMessage('Please enter valid email id.')
        }
        if (!passwordValidator(input.password)) {
            return setErrorRegMessage('Password should have minimum 8 character with the combination of uppercase,lowercase, numbers and specialcharacters.')
        }


        else {
            localStorage.setItem('auth', true);
            navigate('/login');
        }
    }


    const [action, setAction] = useState('');

    const registerLink = () => {
        setAction(' active');
    };

    const loginLink = () => {
        setAction('');
    };

    return (
        <div className={`wrapper${action}`}>
            <div className="form-box login">
                <form action=""
                    onSubmit={formLoginSubmitter}
                >
                    <h1> Login </h1>

                    <div className="input-box">
                        <input type="email" name="email" placeholder="Email"
                            onChange={handleChange}
                        />
                        <FaEnvelope className="icon" />
                    </div>
                    <div className="input-box">
                        <input type="password" name="password" placeholder="Password"
                            onChange={handleChange}
                        />
                        <FaLock className="icon" />
                    </div>

                    <div className="remember-forget">
                        <label><input type="checkbox" />Remeber me</label>
                        <a href="#"> Forget password ? </a>
                    </div>

                    {errorMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "red" }}>{errorMessage}</div>)}
                    {successMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "green" }}>{successMessage}</div>)}


                    <button type="submit"> Login</button>

                    <div className="register-link">
                        <p>Don't have an account? <a href="#"
                            onClick={registerLink}
                        >Register</a></p>
                    </div>

                </form>
            </div>

            <div className="form-box register">
                <form action=""
                    onSubmit={formRegisterSubmitter}
                >
                    <h1> Registration </h1>

                    <div className="input-box">
                        <input type="text" name="text" placeholder="Name"
                            onChange={handleChange}
                        />
                        <FaUser className="icon" />
                    </div>

                    <div className="input-box">
                        <input type="email" name="email" placeholder="Email"
                            onChange={handleChange}
                        />
                        <FaEnvelope className="icon" />
                    </div>

                    <div className="input-box">
                        <input type="password" name="password" placeholder="Password"
                            onChange={handleChange}
                        />
                        <FaLock className="icon" />
                    </div>

                    <div className="remember-forget">
                        <label><input type="checkbox" />I agree to the terms & conditions.</label>
                    </div>

                    {errorRegMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "red" }}>{errorRegMessage}</div>)}
                    {successRegMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "green" }}>{successRegMessage}</div>)}

                    <button type="submit"> Register </button>

                    <div className="register-link">
                        <p>Already have an account? <a href="#"
                            onClick={loginLink}
                        >Login</a></p>
                    </div>

                </form>
            </div>
        </div>
    );
};

export default Login;
