import React, { useState } from 'react'
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from '../Firebase'
import {useNavigate} from 'react-router-dom';
import { current } from '@reduxjs/toolkit';

const Login = () => {
    const [error, setError] = useState(false)
    const [isActive, setIsActive] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    


    const handleLogin = (e) => {
        e.preventDefault();
        signInWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {
                // Signed in 
                const user = userCredential.user;
                navigate("/collection")
                console.log(user);
            })
            .catch((error) => {
                setError(true)
            });
            setIsActive(current => !current)
    }

    // const handleClick1 = event => {
    //     // ðŸ‘‡ï¸ toggle isActive state on click
    //     setIsActive(current => !current);
    //     console.log(setIsActive);
    // };
    // const handleClick2 = event => {
    //     // ðŸ‘‡ï¸ toggle isActive state on click
    //     setIsActive(current => !current);
    //     console.log(setIsActive);
    // };

    return (
        <div className="body">
{/* isActive ? "right-panel-active" :  */}
            <div className={"container"} id="container">
                {/* <div className="form-container sign-up-container">

                    <form action="" className="form">
                        <h1 className="fs-3">Create Account</h1>
                        <div className="social-container">
                            <a href="#" className="social"><i className="fa fa-facebook"></i></a>
                            <a href="#" className="social"><i className="fa fa-google"></i></a>
                            <a href="#" className="social"><i className="fa fa-linkedin"></i></a>
                        </div>
                        <span className="p">or use your email for registration</span>
                        <input type="text" name="name" className="input" placeholder="Name" />
                        <input type="email" name="email" className="input" placeholder="Email" />
                        <input type="password" name="password" className="input" placeholder="Password" />
                        <button className="button" >SignUp</button>
                    </form>
                </div> */}
                <div className="form-container sign-in-container">
                    <form action="#" className="form">
                        <h1 className="h1">Sign In</h1>
                        <div className="social-container">
                            <a href="#" className="social a"><i className="fa fa-facebook"></i></a>
                            <a href="#" className="social a"><i className="fa fa-google"></i></a>
                            <a href="#" className="social a"><i className="fa fa-linkedin"></i></a>
                        </div>
                        <span className="p">or use your account</span>
                        <input type="email" name="email" className="input" placeholder="Email" onChange={(e)=>setEmail(e.target.value)}/>
                        <input type="password" name="password" className="input" placeholder="Password" onChange={(e)=>setPassword(e.target.value)}/>
                        <a href="#" className="a">Forgot Your Password</a>

                        <button className="button" onClick={handleLogin}>LogIn</button>
                        {error && (<span className='mt-2 text-danger'>Wrong credential</span>)}
                    </form>
                </div>
                <div className="overlay-container">
                    <div className="overlay">
                        {/* <div className="overlay-panel overlay-left">
                            <h1 className="h1">Welcome Back!</h1>
                            <p className="p">To keep connected with us please login with your personal info</p>
                            <button className="ghost button" id="signIn" onClick={handleClick2}>Sign In</button>
                        </div> */}
                        <div className="overlay-panel overlay-right">
                            <h1 className="h1">Hello, Friend!</h1>
                            <p className="p">Enter your details and start journey with us</p>
                            {/* <button className="ghost button" id="signUp" onClick={handleClick1}>Sign Up</button> */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login;
