import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { collection, addDoc, deleteDoc, doc, onSnapshot, query } from "firebase/firestore";
import { db } from '../Firebase'
import swal from 'sweetalert';



const CustomerDetailsList = () => {
    const fetchValue = useSelector(state => state.customerDetails.value)
    const [areaData, setAreaData] = useState([])

    const [fetchData, setFetchData] = useState([])
    const [id, setId] = useState()
    const [area, setArea] = useState('')
    const [name, setName] = useState('')
    const [age, setAge] = useState()
    const [gender, setGender] = useState('')
    const [phoneNo, setPhoneNo] = useState('')
    const [altPhoneNo, setAltPhoneNo] = useState('')
    const [lineType, setLineType] = useState('')
    const [address, setAddress] = useState('')
    const [status, setStatus] = useState('')
    const [bussiness, setBussiness] = useState('')
    const [bussinessLocation, setBussinessLocation] = useState('')
    const [file, setFile] = useState();
    const [imgUrl, setImgUrl] = useState();
    const q = query(collection(db, 'CustomerDetails'));

    useEffect(() => {


        onSnapshot(q, (snapshot) => {
            setFetchData(snapshot.docs.map(doc => ({
                id: doc.id,
                item: doc.data()
            })))
        })

        onSnapshot(q, (snapshot) => {
            setAreaData(snapshot.docs.map(doc => ({
                id: doc.id,
                item: doc.data()
            })))
        })

    }, [])


    const editCustomer = (e) => {
        e.preventDefault();

    }

    const onImageChange = (e) => {

        setFile(e.target.files[0]);
    };

    const handleAreaName = (e) => {
        setArea(e.target.value)
    }

    const val = fetchValue.fetchValue



    const handleDelete = (e) => {
        e.preventDefault()
        swal({
            title: "Are you sure?",
            text: "Once deleted, you will not be able to recover this customer details!",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    deleteDoc(doc(db, 'CustomerDetails', id))
                    swal("Poof! Your note has been deleted!", {
                        icon: "success",
                    });
                } else {
                }
            });
    }

    return (
        <div>
            <div className="card mt-3 d-flex" id='customer-card' style={{ boxShadow: "0px 0px 10px" }}>
                {fetchData
                    .filter((item) => {
                        const searchTerm = val.toLowerCase();
                        const name = item.item.name.toLowerCase();
                        // console.log(name);

                        return (
                            (searchTerm &&
                                name.startsWith(searchTerm) &&
                                name !== searchTerm)
                        );
                    })
                    .slice(0, 10)
                    .map((item) => (
                        <>
                            <div className="row d-flex flex-row  m-4">


                                <div className='col-md-4'>
                                    <img src={item.item.imgUrl} className="img-fluid" alt="" style={{ borderRadius: "50%", width: "150px", height: "150px", objectFit: "cover" }} />
                                    <div className="form-grp d-flex flex-row mt-3 align-items-center">
                                        <label className='label'>Name : </label>
                                        <p className='in'>{item.item.name} &nbsp; {item.item.status === "active" ? "ðŸŸ¢" : "ðŸ”´"}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Age : </label>
                                        <p className='in'>{item.item.age}</p>

                                    </div>
                                </div>
                                <div className='col-md-6'>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Phone No : </label>
                                        <p className='in'>{item.item.phoneNo}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Alternative Phone No : </label>
                                        <p className='in'>{item.item.altPhoneNo}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Line Type : </label>
                                        <p className='in'>{item.item.lineType}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Area : </label>
                                        <p className='in'>{item.item.area}</p>

                                    </div>

                                    <div className="form-grp d-flex flex-row ">
                                        <label className='label'>Bussiness : </label>
                                        <p className='in'>{item.item.bussiness}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Bussines Location : </label>
                                        <p className='in'>{item.item.bussinessLocation}</p>

                                    </div>
                                    <div className="form-grp d-flex flex-row">
                                        <label className='label'>Address : </label>
                                        <p className='in'>{item.item.address}</p>

                                    </div>
                                </div>
                                <div className="col-md-2">
                                    <button className='btn btn-outline-warning m-2' data-bs-toggle="modal" data-bs-target="#editCustomerModal">
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                                            <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                                            <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z" />
                                        </svg>
                                    </button>
                                    <button className="btn btn-outline-danger m-2" onClick={(e) => {
                                        swal({
                                            title: "Are you sure?",
                                            text: "Once deleted, you will not be able to recover this customer details!",
                                            icon: "warning",
                                            buttons: true,
                                            dangerMode: true,
                                        })
                                            .then((willDelete) => {
                                                if (willDelete) {
                                                    deleteDoc(doc(db, 'CustomerDetails', item.id))
                                                    swal("Poof! Your note has been deleted!", {
                                                        icon: "success",
                                                    });
                                                } else {
                                                }
                                            });

                                    }}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16"><path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"></path></svg></button>
                                </div>
                            </div>
                            <div className="modal fade" id="editCustomerModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                <div className="modal-dialog modal-dialog-scrollable">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title" id="exampleModalLabel">Edit Customer</h5>
                                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                        </div>
                                        <div className="modal-body">
                                            <form>
                                                <div className="mb-3">
                                                    <img src={file} alt="" className='img-fluid img-thumbnail mx-auto d-block' style={{ borderRadius: "50%", width: "100px", height: "100px" }} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Profile Photo :</label>
                                                    <input type="file" className="form-control" id="recipient-name" onChange={onImageChange} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Customer Name :</label>
                                                    <input type="text" className="form-control" id="recipient-name" value={item.item.name} onChange={(e) => setName(e.target.value)} />
                                                </div>

                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Age :</label>
                                                    <input type="number" className="form-control" id="recipient-name" onChange={(e) => setAge(e.target.value)} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Gender :</label>
                                                    <input type="text" className="form-control" id="recipient-name" onChange={(e) => setGender(e.target.value)} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Mobile No :</label>
                                                    <input type="number" className="form-control" id="recipient-name" onChange={(e) => setPhoneNo(e.target.value)} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Alternative Mobile No :</label>
                                                    <input type="number" className="form-control" id="recipient-name" onChange={(e) => setAltPhoneNo(e.target.value)} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Line Type :</label>
                                                    <select name="line" onChange={(e) => setLineType(e.target.value)} defaultValue="daily">
                                                        <option value="daily">Daily</option>
                                                        <option value="weekly">Weekly</option>
                                                        <option value="monthly">Monthly</option>
                                                    </select>
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="recipient-name" className="col-form-label">Area Name :</label> <br />
                                                    {/* <input type="text" className="form-control" id="recipient-name" /> */}
                                                    {/* <select onChange={handleAreaName} name="fruits" id="fruit-select">
                                                        {areaData.map((option, index) => (
                                                            <option key={index} value={option.item.areaName}>
                                                                {option.item.areaName}
                                                            </option>
                                                        ))}
                                                    </select> */}
                                                    <select >
                                                        <option value={item.item.area}>{item.item.area}</option>
                                                    </select>
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="message-text" className="col-form-label">Address :</label>
                                                    <textarea type="text" className="form-control" id="message-text" onChange={(e) => setAddress(e.target.value)}></textarea>
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="message-text" className="col-form-label">Status :</label> <br />
                                                    <select name="status" onChange={(e) => setStatus(e.target.value)} defaultValue="active">
                                                        <option value="active" >Active</option>
                                                        <option value="inactive">Inactive</option>
                                                    </select>
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="message-text" className="col-form-label">Business :</label>
                                                    <input className="form-control" id="message-text" onChange={(e) => setBussiness(e.target.value)} />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="message-text" className="col-form-label">Business Location :</label>
                                                    <input className="form-control" id="message-text" onChange={(e) => setBussinessLocation(e.target.value)} />
                                                </div>
                                            </form>

                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                            <button type="button" className="btn btn-primary " onClick={editCustomer}>Edit </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </>
                    ))}
            </div>
        </div>
    )
}

export default CustomerDetailsList
