import React, { useEffect, useState } from 'react'


import { useDispatch } from 'react-redux';
import { update } from '../features/customerDetails';



const SearchCustomerDetails = () => {
    const [value, setValue] = useState("");


    const dispatch = useDispatch();

    useEffect(() => {
    }, []);

    const onChange = (event) => {
        dispatch(update({
            fetchValue:event.target.value
         }))
        setValue(event.target.value);
         
    };

    const onSearch = (searchTerm) => {
        setValue(searchTerm);

    };

    return (
        <div className=" d-flex flex-column ">
            <div className="search-container">
                <div className="search-inner d-flex justify-content-around align-items-center p-2">
                    <input type="text" className='search-input ' value={value} onChange={onChange} />
                    <button className='btn btn-success btn-sm ms-2' onClick={() => onSearch(value)}> Search </button>
                </div>
   
             
            </div>
           
        </div>
    );
}

export default SearchCustomerDetails
