import {createSlice} from '@reduxjs/toolkit'

export const collectionSlice = createSlice({
    name: 'collection',
    initialState : {value : {
        line : "",date:"",bringAmount:0,totalIncome:0,totalLoan:0,total:0,type:"",customerName: "",amount:0
    }},
    reducers : {
        input : (state,action) => {
            state.value = action.payload
        }
    }
})

export const {input} = collectionSlice.actions;
export default collectionSlice.reducer;
