import {createSlice} from '@reduxjs/toolkit'

export const noteSlice = createSlice({
    name: 'notes',
    initialState : {value : {
        isNotesClicked : false
    }},
    reducers : {
        click : (state,action) => {
            state.value = action.payload
        }
    }
})

export const {click} = noteSlice.actions;
export default noteSlice.reducer;
