import React, { useEffect, useState } from 'react'
import { collection, addDoc, deleteDoc,doc, onSnapshot, query } from "firebase/firestore";
import { db } from '../Firebase'
import swal from 'sweetalert';



const SearchArea = () => {
    const [value, setValue] = useState("");
    const q = query(collection(db, 'Area'));
    const [fetchData, setFetchData] = useState([])
    const [areaName, setAreaName] = useState('')
    const [district, setDistrict] = useState('')
    const [city, setCity] = useState('')

    useEffect(() => {

        onSnapshot(q, (snapshot) => {
            setFetchData(snapshot.docs.map(doc => ({
                id: doc.id,
                item: doc.data()
            })))
        })
        console.log(fetchData);
    }, [areaName]);



    const handleAreaName = (e) => {
        setAreaName(e.target.value)
    }
    const handleDistrict = (e) => {
        setDistrict(e.target.value)
    }
    const handleCity = (e) => {
        setCity(e.target.value)
    }

    const handleSave = async (e) => {
        swal({
            title: "Success!",
            text: "Your Note has been updated successfully!",
            icon: "success",
            button: "ok!",
        });
        try {
            const docRef = await addDoc(collection(db, "Area"), {
                areaName: areaName,
                district: district,
                city: city
            });

        } catch (e) {
            console.error("Error Updating document: ", e);
        }
    }
    // const handleSubmit = (e) => {
    //     e.preventDefault()
    //     swal({
    //         title: "Are you sure?",
    //         text: "Once deleted, you will not be able to recover this area!",
    //         icon: "warning",
    //         buttons: true,
    //         dangerMode: true,
    //       })
    //       .then((willDelete) => {
    //         if (willDelete) {
    //             deleteDoc(doc(db,'Area',doc.id))
    //           swal("Poof! Area has been deleted!", {
    //             icon: "success",
    //           });
    //         } else {
    //         //   swal("Your imaginary file is safe!");
    //         }
    //       });
    //    } 

    const onChange = (event) => {
        setValue(event.target.value);
    };

    const onSearch = (searchTerm) => {
        setValue(searchTerm);

    };

    return (
        <div className="App">
            <div className="search-container">
                <div className="search-inner d-flex justify-content-around align-items-center p-2">
                    <input type="text" className='search-input ' value={value} onChange={onChange} />
                    <button className='btn btn-success btn-sm ms-2' onClick={() => onSearch(value)}> Search </button>
                </div>
                {/* {fetchData.map((x)=>(
            <div>{x.item.areaName}</div>
          ))} */}
                <div className="dropdown">
                    {fetchData
                        .filter((item) => {
                            const searchTerm = value.toLowerCase();
                            const name = item.item.areaName.toLowerCase();
                            // console.log(name);

                            return (
                                (searchTerm &&
                                    name.startsWith(searchTerm) &&
                                    name !== searchTerm)
                            );
                        })
                        .slice(0, 10)
                        .map((item) => (
                            <>
                                <div>
                                    <p
                                        onClick={() => onSearch(item.item.areaName)}
                                        className="dropdown-row" key={item.id}
                                    >
                                        {item.item.areaName}, {item.item.district} - {item.item.city}
                                    </p>
                                    <button className="btn btn-outline-danger m-2" data-bs-toggle="modal" data-bs-target="#deleteCustomerModal" onClick={(e) => deleteDoc(doc(db,'Area',item.id))}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16"><path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"></path></svg></button>
                                </div>
                                <div className="modal fade" id="editAreaModal" tabIndex="-1" aria-labelledby="exampleModalLabel" data-backdrop="static" aria-hidden="true">
                                    <div className="modal-dialog">
                                        <div className="modal-content">
                                            <div className="modal-header">
                                                <h5 className="modal-title" id="exampleModalLabel">Area Details</h5>
                                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                            </div>

                                            <div className="modal-body">
                                                <form>
                                                    <div className="mb-3">
                                                        <label htmlFor="recipient-name" className="col-form-label">Area Name :</label>
                                                        <input type="text" className="form-control" id="recipient-name" onChange={handleAreaName}  />
                                                    </div>
                                                    <div className="mb-3">
                                                        <label htmlFor="message-text" className="col-form-label">District :</label>
                                                        <input type="text" className="form-control" id="message-text" onChange={handleDistrict}  />
                                                    </div>
                                                    <div className="mb-3">
                                                        <label htmlFor="message-text" className="col-form-label">City :</label>
                                                        <input className="form-control" id="message-text"  onChange={(e) => setCity(e.target.value)} />
                                                    </div>
                                                </form>
                                            </div>
                                            <div className="modal-footer">
                                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                                <button type="button" className="btn btn-primary" onClick={handleSave}>Update</button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </>
                        ))}
                </div>
            </div>
        </div>
    );
}

export default SearchArea
