import React from "react";
import './Login.css';
import { FaUser, FaLock } from "react-icons/fa";
import { emailValidator, passwordValidator } from "./regexValidator";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const navigate=useNavigate();
    const [input, setInput] = React.useState({ email: '', password: '' })
    const [errorMessage, setErrorMessage] = React.useState('')
    const [successMessage, setSuccessMessage] = React.useState('')

    const handleChange = (e) => {
        setInput({ ...input, [e.target.name]: e.target.value })
    }

    React.useEffect(()=>{
    if(localStorage.getItem('auth'))
        { navigate('/')}
},[])

    const formSubmitter = (e) => {
        e.preventDefault();
        setSuccessMessage('');
        if (!emailValidator(input.email)) {
            return setErrorMessage('Please enter valid email id.')
        }
        if (!passwordValidator(input.password)) {
            return setErrorMessage('Password should have minimum 8 character with the combination of uppercase,lowercase, numbers and specialcharacters.')
        }
        if (input.email !== "admin@a.com" || input.password !== "Password@1"){
            setErrorMessage('Invalid email or password.');
        }
        else { 
            localStorage.setItem('auth',true);
            navigate('/'); 
        }
    }

    return (
        <div className='wrapper'>
            <div className="form-box login">
                <form action="" onSubmit={formSubmitter}>
                    <h1> Login </h1>


                    <div className="input-box">
                        <input type="text" name="email" placeholder="Username" onChange={handleChange} />
                        <FaUser className="icon" />
                    </div>
                    <div className="input-box">
                        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
                        <FaLock className="icon" />
                    </div>

                    <div className="remember-forget">
                        <label><input type="checkbox" /></label>
                        <a href="#"> Forget password ? </a>
                    </div>

                    {errorMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "red" }}>{errorMessage}</div>)}
                    {successMessage.length > 0 && (<div style={{ textAlign: "center", marginBottom: "10px", marginTop: "10px", color: "green" }}>{successMessage}</div>)}


                    <button type="submit"> Login</button>

                </form>
            </div>
        </div>
    );
};

export default Login;
