import * as React from 'react';
import { Link, useLocation, Outlet, useNavigate } from 'react-router-dom';
import { styled, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import MuiDrawer from '@mui/material/Drawer';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import CssBaseline from '@mui/material/CssBaseline';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import HomeIcon from '@mui/icons-material/Home';
import PersonIcon from '@mui/icons-material/Person';
import AssessmentIcon from '@mui/icons-material/Assessment';
import { Button } from "@mui/material";
import LogoutIcon from '@mui/icons-material/Logout';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Popover from '@mui/material/Popover';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import AccountBoxIcon from '@mui/icons-material/AccountBox';
import MailIcon from '@mui/icons-material/Mail';
import EditNoteIcon from '@mui/icons-material/EditNote';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import axios from "axios";

const drawerWidth = 240;

const openedMixin = (theme) => ({
    width: drawerWidth,
    transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
    }),
    overflowX: 'hidden',
});

const closedMixin = (theme) => ({
    transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: 'hidden',
    width: `calc(${theme.spacing(7)} + 1px)`,
    [theme.breakpoints.up('sm')]: {
        width: `calc(${theme.spacing(8)} + 1px)`,
    },
});

const DrawerHeader = styled('div')(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
}));

const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    }),
}));

const Drawer = styled(MuiDrawer, { shouldForwardProp: (prop) => prop !== 'open' })(
    ({ theme, open }) => ({
        width: drawerWidth,
        flexShrink: 0,
        whiteSpace: 'nowrap',
        boxSizing: 'border-box',
        ...(open && {
            ...openedMixin(theme),
            '& .MuiDrawer-paper': openedMixin(theme),
        }),
        ...(!open && {
            ...closedMixin(theme),
            '& .MuiDrawer-paper': closedMixin(theme),
        }),
    }),
);


export default function Navbar() {

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        LoginDetails();
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const openPop = Boolean(anchorEl);
    const id = openPop ? 'simple-popover' : undefined;


    const location = useLocation();
    const path = location.pathname;

    const theme = useTheme();
    const [open, setOpen] = React.useState(false);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const menu = [
        { title: 'Home', path: '/home', icon: <HomeIcon /> },
        { title: 'Users', path: '/users', icon: <PersonIcon /> },
        { title: 'Reports', path: '/reports', icon: <AssessmentIcon /> }
    ];

    const navigate = useNavigate();
    const [logout, setLogout] = React.useState(false);
    const [userdata, setUserdata] = React.useState({
        Name: '',
        Email: '',
        Userid: 0,
        Token: '',
        Password: ''
    });

    React.useEffect(() => {
        LoginDetails();
    }, []);

    function LoginDetails() {
        const data = {
            LoginToken: localStorage.getItem('auth'),
        }
        const url = 'https://localhost:44374/api/Login/loginDetails';
        axios.post(url, data).then((result) => {
            const res = JSON.parse(result.data);

            userdata.Name = res[0].Name;
            userdata.Email = res[0].Email;
            userdata.Userid = res[0].UserId;
            userdata.Token = res[0].LoginToken;
            userdata.Password = res[0].Password;

            var pwd = '';
            let count = res[0].Password.length ;
            console.log('count',res[0].Password.length);
            for(let i=0; i<count ;i++){
                pwd+='*';
                console.log("a",pwd);
            }

            const pass ={
                "PasswordVisible" : res[0].Password ,
                "PasswordHidden" : pwd
            }

            setPwd(pass);

        }
        )
    }

    const logoutHandler = (e) => {
        e.preventDefault();

        const data = {
            LoginToken: localStorage.getItem('auth'),
        }
        const url = 'https://localhost:44374/api/Login/logout';
        axios.put(url, data).then((result) => {
            const res = JSON.parse(result.data);
            if (res[0].IsLogout == "true") {
                localStorage.removeItem("auth");
                setLogout(true);
                if (!localStorage.getItem('auth')) {
                    navigate("/");
                }
            }
        }
        )
    }

    const [pwdvisible,setPwdvisible] = React.useState(false);
    const [pwd, setPwd] = React.useState([]);

    const onVisiblePWD = () => {
        setPwdvisible(false);
    }
    const onNotvisiblePWD = () => {
        setPwdvisible(true);
    }

    return (
        <Box sx={{ display: 'flex' }}>
            <CssBaseline />
            <AppBar position="fixed" open={open}>
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        onClick={handleDrawerOpen}
                        edge="start"
                        sx={{
                            marginRight: 5,
                            ...(open && { display: 'none' }),
                        }}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
                        Bill Board
                    </Typography>
                    <Button
                        aria-describedby={id}
                        onClick={handleClick}
                        color="inherit"
                        style={{ border: '1px solid white' }}
                        sx={{
                            width: '20vh', height: '4vh', borderRadius: '30px'
                        }}
                    >
                        <AccountCircleIcon />
                        &nbsp;Profile
                    </Button>
                    <Button
                        color="inherit"
                        onClick={logoutHandler}
                        style={{ border: '1px solid white' }}
                        sx={{
                            width: '20vh', height: '4vh', borderRadius: '30px', ml: 2
                        }}
                    >
                        <LogoutIcon />
                        &nbsp;Logout
                    </Button>
                </Toolbar>
            </AppBar>
            <Popover
                id={id}
                open={openPop}
                anchorEl={anchorEl}
                onClose={handleClose}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'left',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
            >
                <Box sx={{ flexGrow: 1, p: 1 }}>
                    <Grid>
                        <Paper sx={{ p: 1, m: 1, width: '55vh', height: '10vh' }} >
                            <div style={{ display: 'flex' }}>
                                <AccountBoxIcon style={{ fontSize: '25px', color: '#808080' }} />
                                <div style={{ fontSize: '15px', color: '#808080', marginLeft: '2%' }}>Name</div>
                            </div>
                            <div style={{ float: 'right', fontSize: '15px', color: '#9c27b0' }}>
                                {userdata.Name}
                            </div>
                        </Paper>
                        <Paper sx={{ p: 1, m: 1, width: '55vh', height: '10vh' }} >
                            <div style={{ display: 'flex' }}>
                                <MailIcon style={{ fontSize: '25px', color: '#808080' }} />
                                <div style={{ fontSize: '15px', color: '#808080', marginLeft: '2%' }}>Email</div>
                            </div>
                            <div style={{ float: 'right', fontSize: '15px', color: '#9c27b0' }}>
                                {userdata.Email}
                            </div>
                        </Paper>
                        <Paper sx={{ p: 1, m: 1, width: '55vh', height: '10vh' }} >
                            <div style={{ display: 'flex' }}>
                                <AccountBoxIcon style={{ fontSize: '25px', color: '#808080' }} />
                                <div style={{ fontSize: '15px', color: '#808080', marginLeft: '2%' }}>Password</div>
                                <div style={{marginLeft: '60%'}}>
                                    <VisibilityIcon 
                                    style={{color: '#808080',display: pwdvisible == true ? '' : 'none' }}
                                    onClick={onVisiblePWD}
                                    />
                                    <VisibilityOffIcon 
                                    style={{color: '#808080',display: pwdvisible == false ? '' : 'none' }}
                                    onClick={onNotvisiblePWD}
                                    />
                                </div>
                            </div>
                            <div style={{ float: 'right', fontSize: '15px', color: '#9c27b0' }}>
                                {userdata.Password}
                            </div>
                        </Paper>
                        <div style={{display:'flex', alignItems:'center', justifyContent:'center'}}>
                            <Button
                                variant="contained"
                                color="primary"
                                style={{ border: '1px solid #1976d2' }}
                                sx={{
                                    width: '15vh', height: '5vh', borderRadius: '30px'
                                }}
                            >
                                <EditNoteIcon />
                                &nbsp;Edit
                            </Button>
                        </div>
                    </Grid>
                </Box>
            </Popover >
            <Drawer variant="permanent" open={open}>
                <DrawerHeader style={{ backgroundColor: '#1976d2' }}>
                    <span style={{ width: "100%", color: "white", marginLeft: "5%" }}> Hi <br /> {userdata.Name}  </span>
                    <IconButton onClick={handleDrawerClose} style={{ color: 'white' }}>
                        {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
                    </IconButton>
                </DrawerHeader>
                <Divider />
                <List>
                    {menu.map((item, index) => (
                        <ListItem
                            key={item.title}
                            disablePadding
                            component={Link}
                            to={item.path}
                            button
                            selected={item.path === path}
                            sx={{ display: 'block' }}
                        >
                            <ListItemButton
                                sx={{
                                    minHeight: 48,
                                    justifyContent: open ? 'initial' : 'center',
                                    px: 2.5,
                                }}
                            >
                                <ListItemIcon
                                    sx={{
                                        minWidth: 0,
                                        mr: open ? 3 : 'auto',
                                        justifyContent: 'center',
                                    }}
                                >
                                    {item.icon}
                                </ListItemIcon>
                                <ListItemText primary={item.title} sx={{ opacity: open ? 1 : 0 }} />
                            </ListItemButton>
                        </ListItem>
                    ))}
                </List>

            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                <DrawerHeader />
                <Outlet />
            </Box>
        </Box >
    );
}
