import React, { useState } from 'react'
import SearchCustomer from './SearchCustomer'
import swal from 'sweetalert';
import { useDispatch, useSelector } from 'react-redux';
import { input } from '../features/collection'; 
import { doc, setDoc } from "firebase/firestore"; 
import { db } from '../Firebase'


const CollectionCatBar = () => {

    const [action,setAction] = useState('')
    const dispatch = useDispatch();
    const details = useSelector(state => state.collection.value)
    console.log(details);
    // console.log(details.date.toString());

    const handlePayLoan = async() => {
        dispatch(input({
            type:"loan"
        }))
        

        var date = new Date(); // M-D-YYYY

		var d = date.getDate();
		var m = date.getMonth() + 1;
		var y = date.getFullYear();

		var dateString = y + '-' + (m <= 9 ? '0' + m : m) + '-' + (d <= 9 ? '0' + d : d).toString();;

        console.log(dateString);
        console.log(details.name);
        console.log(details.type);
        console.log(details.amount);
        try{
            await setDoc(doc(db, "TransactionDetails", dateString), {
                date: dateString,
                customerName: details.name,
                type: details.type,
                amount: details.amount
              });
              swal({
                title: "Success!",
                text: "Amount Paid Successfully!",
                icon: "success",
                button: "ok",
              });
        }
        catch(err) {
            console.log(err);
        }
    }

    const selectAction = (e) => {
        setAction(e)
    }
    const doNtg = () => {
        
    }

    const handleLoanAmount =(e) => {
        e.preventDefault();
        dispatch(input({
            amount: e.target.value
        }))
    }


    // console.log(details.amount);


    return (
        <div>
            <div className="d-grid gap-5 d-flex flex-row justify-content-md-center mt-5 ">
                <button className="btn btn-danger" type="button" data-bs-toggle="modal" data-bs-target="#payModal">Loan</button>
                <button className="btn btn-success" type="button" data-bs-toggle="modal" data-bs-target="#collectModal">Collect</button>
                <button className="btn btn-warning" type="button" data-bs-toggle="modal" data-bs-target="#transactionModal">Transcation</button> 
            </div>

            {/* PayModel */}
            
            <div className="modal fade" id="payModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"> Loan Details</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form action="" id='payModal' className='d-flex flex-column justify-content-around align-items-center my-2'>
                                <div className="d-flex justify-content-around align-items-center my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Customer Name :</label>
                                     <SearchCustomer />        
                                </div>
                             
                                <div className="d-flex justify-content-around align-items-center my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Loan Amount :</label>
                                    <input type="number" onChange={handleLoanAmount}/>       
                                </div>
                                <div className="d-flex justify-content-around align-items-center my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Date :</label>
                                    <input type="date" value={details.date} onChange={doNtg}/>       
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-danger" onClick={handlePayLoan}>Pay</button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Collect Modal */}

            <div className="modal fade" id="collectModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"> Collect Amount</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form action="" id='payModal' className='d-flex flex-column justify-content-center align-items-center my-2'>
                                <div className="d-flex my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Customer Name :</label>
                                     <SearchCustomer />        
                                </div>
                         
                                <div className="d-flex my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Collection Amount :</label>
                                    <input type="number" />       
                                </div>
                                <div className="d-flex my-2">
                                    <label htmlFor="customerName" className='text-start payLabel label'>Date :</label>
                                    <input type="date" value={details.date}/>       
                                </div>
                                <div className="d-flex my-2">
                                    <label htmlFor="action" className='form-label label my-3'>Action : </label>
                                    <select defaultValue="" className="form-select form-select-sm w-50 mx-5 my-3" aria-label=".form-select-sm example" onChange={(e) => selectAction(e.target.value)}>
                                    <option value=""  >Open this select menu</option>
                                    <option value="paid">Paid</option>
                                    <option value="closed">Closed</option>
                                    </select>
                                </div>

                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-danger" onClick={handlePayLoan}>Collect</button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Transaction Model */}

            <div className="modal fade" id="transactionModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"> Transaction History</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                           <div className="container">
                            <div className="row">
                                <div className="col-md-6">
                                    Customer Name :
                                    Customer Id : 
                                    Date : 
                                </div>
                                <div className="col-md-6">
                                    Amount:
                                    Method : 
                                </div>
                            </div>
                           </div>
                        </div>
                        
                    </div>
