import React from 'react'
import { doc, deleteDoc } from "firebase/firestore";
import {db} from "../Firebase"
import swal from 'sweetalert';


const NotesList = ({arr}) => {

   const handleSubmit = (e) => {
    e.preventDefault()
    swal({
        title: "Are you sure?",
        text: "Once deleted, you will not be able to recover this note!",
        icon: "warning",
        buttons: true,
        dangerMode: true,
      })
      .then((willDelete) => {
        if (willDelete) {
            deleteDoc(doc(db,'Notes',arr.id))
          swal("Poof! Your note has been deleted!", {
            icon: "success",
          });
        } else {
        //   swal("Your imaginary file is safe!");
        }
      });
   } 

  return (
    <div className=' m-3 '  >
        <div className="card text-bg-secondary d-flex" id="notes-card">
            <div className="card-body d-flex flex-row justify-content-around align-items-center">
                <div>

                <h5 className="card-title">{arr.item.title}</h5>
                <p className="card-text">{arr.item.description}</p>
                </div> 
                <div >
                <button className="btn btn-danger m-2 d-flex justify-content-end align-items-end" id='note-dlt-btn' onClick={handleSubmit} style={{marginRight:"0px"}} data-bs-toggle="modal" data-bs-target="#deleteCustomerModal"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16"><path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"></path></svg></button>
                </div>
            </div>
            <div className="card-footer text-end">
                <p className="card-text">{arr.item.date}</p>
            </div>
        </div>
       
    </div>
  )
}

export default NotesList
